INSERT INTO `syssql` (`ID`, `UseFor`, `Description`, `ObjectName`, `ControlName`, `MariaDB`, `MariaDB_NoPara`) VALUES
	(1, 'Other', 'right  系统启动时给管理员添加基本权限', NULL, NULL, 'replace into sysuserright (fUserID,fRightID,fHasRight)\r\nselect 1,fNMID,1 from sysnavigationmenus where fDefault=1 or fMenuText=\'User\' or fMenuText=\'Setup\' or fParentId=(select fNMID from sysnavigationmenus where fMenuText=\'User\');', NULL),
	(2, 'Other', 'right  给普通用户添加默认权限', NULL, NULL, 'replace into sysuserright (fUserID,fRightID,fHasRight)\r\nselect @Para,fNMID,fDefault from sysnavigationmenus where fDefault=1 ;', NULL),
	(3, 'Other', 'right  执行加入用户权限到权限表且不覆盖已经有的记录', NULL, NULL, 'INSERT IGNORE sysuserright (fUserID,fRightID,fHasRight)  select @Para, fNMID, 0 from sysnavigationmenus where fNMID<>1 and fMenuText<>\'Exit\' and fEnabled=1', NULL),
	(4, 'Other', 'ADO方式建立临时表额SQL', NULL, NULL, NULL, NULL),
	(6, 'Form', '列表窗体数据源', 'f_Order_List', 'Form', 'SELECT  \r\nfOrderID,fOrderDate,fCustomerID,fCustomerName,fCity,fSubmited1,fSubmit_Name,fAmount,fSubmited+0 as fSubmited,fRequiredDeliveryDate,\r\nfDesconto,fTax,fPayable,fContato,fCelular from v_order as o\r\nWHERE fOrderID Like \'\\@Para%\' and fCanceled=0 and \\@Para AND (fSubmited=@Para OR fSubmited=@Para) \r\nORDER BY forderID DESC', 'SELECT  \r\nfOrderID,fOrderDate,fCustomerID,fCustomerName,fCity,fSubmited1,fSubmit_Name,fAmount,fSubmited+0 as fSubmited,fRequiredDeliveryDate,\r\nfDesconto,fTax,fPayable,fContato,fCelular from v_order as o\r\nWHERE fOrderID Like \'\\@Para%\' and fCanceled=0'),
	(12, 'Report', '订单打印数据源', 'r_Order', NULL, 'SELECT Q.*, Q2.*\r\nFROM (select LianCi from (select 1 as LianCi union select 2 as LianCi) as Q1 where Lianci<=@Para)  AS Q, (SELECT d.ID, o.OrderID, o.CustomerName, o.KeHuHaoMa, o.ShuiHao, o.ChengShi, o.RiQi,o.JiaoHuoRiQi, o.LeiBie, o.PinPaiCaiLiao, o.JinE AS ZongJinE, o.Shui, o.YingFuJinE, o.Desconto, o.Note,d.ShuLiang, d.PinMing, d.Chang, d.Kuan, d.DanJia, d.JinE AS MingXiJinE FROM t_order AS o LEFT JOIN t_order_detail AS d ON o.OrderID = d.OrderID WHERE o.OrderID=@Para)  AS Q2', NULL),
	(13, 'Report', '报表数据源', 'r_InkjetPrintingGuide', NULL, 'SELECT p.*, cp.ChanPinMingCheng, cp.WuLiaoBianHao, cp.ShuLiang, cp.Chang, cp.Kuan, cp.ChanPinID, o.CustomerName, o.KeHuHaoMa, o.ShuiHao, o.ChengShi, o.RiQi AS DingDanRiQi, \r\niif(p.KeZi=1,"是SIM","") as KeZi1, \r\n iif(p.FuMo=1,"是SIM","") as FuMo1, \r\niif(p.DaKou=1,"是SIM","") as DaKou1, \r\niif(p.LiuBian=1,"是SIM","") as LiuBian1, iif(p.DaoKeAnZhuang=1,"是SIM","") as DaoKeAnZhuang1\r\nFROM (t_InkjetPrintingGuide AS p LEFT JOIN t_InkjetPrintingGuide_ChanPin AS cp ON p.InkjetPrintingGuide_ID = cp.InkjetPrintingGuide_ID) LEFT JOIN t_order AS o ON p.OrderID = o.OrderID\r\nWHERE (((p.InkjetPrintingGuide_ID)=\'@PK\'))\r\nORDER BY cp.ChanPinID', NULL),
	(14, 'Form', '付款单列表数据源', 'f_PaymentOrder_List', 'Form', 'select fOrderID,fCustomerID,fContato,fCelular,fTelefone,fConfirmed,fConfirmed1,fAmount,fDesconto,fTax,fPayable,fOrderDate,fConfirm_Name,\r\nfCustomerName,fNUIT,fCity,fEntry_Name\r\nfrom v_order where fCanceled=0 and fSubmited=1\r\nand \\@Para AND (fConfirmed=@Para OR fConfirmed=@Para)\r\nORDER BY fOrderID DESC', 'select fOrderID,fOrderDate,fCustomerID,fContato,fCelular,fTelefone,fConfirmed,fConfirmed1,fAmount,fDesconto,fTax,fPayable,fConfirm_Name,\r\nfCustomerName,fNUIT,fCity,fEntry_Name\r\nfrom v_order where fCanceled=0 and fSubmited=1\r\nORDER BY fOrderID DESC'),
	(15, 'Form', '窗体数据源', 'f_Complete_List', 'Form', 'select fOrderID,fOrderDate,fCustomerName,fDelivered1,fDeliverer_Name,fDelivered,fCity,fEndereco,fContato,fCelular,fTelefone\r\nfrom v_order\r\nWHERE fConfirmed=1 AND fCanceled=0 And \\@Para AND (fDelivered=@Para OR fDelivered=@Para)\r\nORDER BY fOrderDate DESC', 'select fOrderID,fOrderDate,fCustomerName,fDelivered1,fDeliverer_Name,fDelivered,fCity,fEndereco,fContato,fCelular,fTelefone\r\nfrom v_order\r\nWHERE fConfirmed=1 AND fCanceled=0'),
	(17, 'Other', '检查用户权限用SQL', NULL, NULL, 'SELECT Q.fObjectName,if(isnull(r.fHasRight),0,r.fHasRight+0) as HasRight\r\nFROM (\r\nSELECT n.fNMID, n.fObjectName, @Para AS fUserID\r\nFROM sysnavigationmenus AS n\r\nWHERE fParentId=(\r\nSELECT fNMID\r\nFROM sysnavigationmenus\r\nWHERE fObjectName=@Para AND fisCommandButton=0)) AS Q\r\nLEFT JOIN sysuserright AS r ON (Q.fNMID=r.fRightID AND Q.fUserID=r.fUserID)', NULL),
	(18, 'Other', '读取某一用户树节点 带权限', NULL, NULL, 'SELECT n.*\r\nFROM (\r\nSELECT @Para AS `fUserID`, t.*\r\nFROM sysnavigationmenus AS t where t.fIsCommandButton=0) AS n\r\nLEFT JOIN sysuserright AS r ON n.fNMID=r.fRightID AND n.fUserID=r.fUserID where r.fHasRight=1\r\nORDER BY n.fDispIndex', NULL),
	(19, 'Form', '窗体数据源', 'f_Adjustment_list', 'Form', 'select fOrderID,fOrderDate,fCustomerID,fCustomerName,fCanceled1,fCancel_Name,fSubmited1,fSubmit_Name,fConfirmed1,\r\nfConfirm_Name,fDelivered1,fDeliverer_Name,fAmount,fTax,fPayable,fDesconto,fCanceled\r\nfrom v_order\r\nWHERE \\@Para AND (fCanceled=@Para Or fCanceled=@Para)\r\nORDER BY fOrderID DESC', 'select fOrderID,fOrderDate,fCustomerID,fCustomerName,fCanceled1,fCancel_Name,fSubmited1,fSubmit_Name,fConfirmed1,\r\nfConfirm_Name,fDelivered1,fDeliverer_Name,fAmount,fTax,fPayable,fDesconto,fCanceled\r\nfrom v_order\r\nORDER BY fOrderID DESC'),
	(21, 'Form', '窗体数据源', 'f_Receivables_list', NULL, 'SELECT Q1.*, if(isnull(YingFuJinE),0,round(YingFuJinE,2))-if(isnull(AmountHJ),0,round(AmountHJ,2)) as QianKuan\r\nFROM (\r\nSELECT OrderID, CustomerName, Desconto, JinE, Shui, YingFuJinE, RiQi AS DingDanRiQi, \r\nKeHuHaoMa, ShuiHao, ChengShi, JiaoHuoRiQi, IF(JiaoFu,\'是SIM\',\'\') AS JiaoFu1, JiaoFuRiQi,\r\n(\r\nSELECT SUM(Amount)\r\nFROM t_receivables AS r\r\nWHERE r.OrderID=o.OrderID) AS AmountHJ,\r\nu.Username AS JiaoFuRen\r\nFROM t_order AS o\r\nLEFT JOIN sysusers AS u ON o.JiaoFuRenID = u.UserID\r\nWHERE \\@Para\r\n) AS Q1\r\nORDER BY DingDanRiQi DESC', NULL),
	(22, 'Form', '收款记录子窗体数据源', 'f_Receivables_DetailList', NULL, 'SELECT t.OrderID, t.CustomerName, t.KeHuHaoMa, t.ShuiHao, t.ChengShi, t.RiQi, t.JinE, t.Shui, t.Desconto, t.YingFuJinE, t.YingFuJinE AS YiFuJinE, r.ShouKuanFangshi, r.ShouKuanRiQi, r.Amount, r.BeiZhu, u.Username AS ShouKuanName\r\nFROM (t_receivables AS r INNER JOIN t_order AS t ON r.OrderID = t.OrderID) LEFT JOIN sysusers AS u ON r.ShouKuanRen = u.UserID\r\nWHERE (((t.OrderID)=@Para))\r\nORDER BY r.ShouKuanRiQi DESC', NULL),
	(23, 'Report', '收款日报表数据源', 'r_RecibidoDailyRreport', NULL, 'select Q1.*,Q1.YS-Q1.SK as QK\r\nfrom (select r.fID,r.fReceiptDate,r.fCustomerID,r.fPaymentMethodID,r.fAmountCollected,r.fPayeeID,r.fNote,c.fCustomerName,e.fTitle as fPaymentMethod,\r\n(select sum(fPayable) from t_order as o where o.fCustomerID=r.fCustomerID and o.fSubmited=1 and o.fCanceled=0 and o.fConfirmed=1) as YS,\r\n(select sum(r1.fAmountCollected ) from t_receivables as r1 where r1.fID<=r.fid) as SK\r\nfrom  t_receivables as r \r\nleft join t_customer as c on r.fCustomerID=c.fCustomerID\r\nleft join t_enumeration as e on e.fItemID=r.fPaymentMethodID\r\nwhere fReceiptDate=@Para) as Q1', NULL),
	(25, 'Form', '每日收款统计报表 基于收款', 'f_report_rec_day_List', 'Form', 'select if(isnull(Q3.d),\'Sum\',Q3.d) as Day0 ,M1,M2,M3,M4,M5,M6,M7,M8,M9,M10,M11,M12\r\nfrom (select Q2.d, \r\nFormat(SUM(Q2.M1_),2) AS M1, \r\nformat(SUM(Q2.M2_),2)   AS M2, \r\nFormat(SUM(Q2.M3_),2)  AS M3, \r\nFormat(SUM(Q2.M4_),2)  AS M4, \r\nFormat(SUM(Q2.M5_),2)  AS M5, \r\nFormat(SUM(Q2.M6_),2)  AS M6, \r\nFormat(SUM(Q2.M7_),2)  AS M7, \r\nFormat(SUM(Q2.M8_),2)  AS M8, \r\nFormat(SUM(Q2.M9_),2)  AS M9, \r\nFormat(SUM(Q2.M10_),2)  AS M10, \r\nFormat(SUM(Q2.M11_),2)  AS M11, \r\nFormat(SUM(Q2.M12_),2)  AS M12\r\n\r\nfrom (select Q1.d,\r\nIF(Q1.m=1,Q1.j1, NULL) AS M1_,\r\nIF(Q1.m=2,Q1.j1, NULL) AS M2_,\r\nIF(Q1.m=3,Q1.j1, NULL) AS M3_,\r\nIF(Q1.m=4,Q1.j1, NULL) AS M4_,\r\nIF(Q1.m=5,Q1.j1, NULL) AS M5_,\r\nIF(Q1.m=6,Q1.j1, NULL) AS M6_,\r\nIF(Q1.m=7,Q1.j1, NULL) AS M7_,\r\nIF(Q1.m=8,Q1.j1, NULL) AS M8_,\r\nIF(Q1.m=9,Q1.j1, NULL) AS M9_,\r\nIF(Q1.m=10,Q1.j1, NULL) AS M10_,\r\nIF(Q1.m=11,Q1.j1, NULL) AS M11_,\r\nIF(Q1.m=12,Q1.j1, NULL) AS M12_\r\nfrom (SELECT MONTH(fReceiptDate) AS m, DAY(fReceiptDate) AS d, SUM(fAmountCollected) AS j1\r\nFROM t_receivables\r\nWHERE  Year(fReceiptDate)=@Para\r\nGROUP BY MONTH(fReceiptDate), DAY(fReceiptDate)) AS Q1) As Q2\r\ngroup by Q2.d WITH ROLLUP) as Q3', NULL),
	(26, 'Form', '窗体数据源', 'f_Receivables_TodayList', NULL, 'SELECT r.ID,r.ShouKuanRiQi, r.ShouKuanFangShi,r.ShouKuanRen, r.Amount, r.BeiZhu, u.Username\r\nFROM t_receivables AS r LEFT JOIN sysusers AS u ON r.ShouKuanRen = u.UserID\r\nWHERE r.ShouKuanRiQi BETWEEN @Para and @Para\r\nORDER BY r.ID DESC', NULL),
	(31, 'Form', '取得目标数据库所有表名', 'SysfrmDeveloper_ListFromCreater', 'TabelName', 'show tables', NULL),
	(33, 'Other', '全部系统及用户错误信息', NULL, NULL, 'select \r\nfID, \r\nif(isnull(\\@Para),fCaption,\\@Para) as Lan  \r\nfrom \r\n(select fID,fParentId,fCaption,fLanguage1,fLanguage2 from syslanguage as a where (a.fParentId=2 or a.fParentId=101) and (not isNull(fCaption))) as Q1', NULL),
	(34, 'Other', '按关键字查询错误信息', NULL, NULL, 'SELECT *\r\nFROM (\r\nSELECT fID,fCaption AS fDefualt,fLanguage1,fLanguage2\r\nFROM syslanguage\r\nWHERE fParentId=@Para AND NOT ISNULL(fCaption)) AS Q\r\nWHERE CONCAT(fDefualt,fLanguage1,fLanguage2) LIKE \'%\\@Para%\'', NULL),
	(35, 'Form', '翻译信息列表窗体数据源', 'SysfrmLanguage_sub', 'Form', 'select fid, \r\ncase ftype when 100 then \'Label\' when 104 then \'Button\' end as CtlType, fcaption,fLanguage1,fLanguage2 \r\nfrom syslanguage \r\nwhere not isnull(fcaption) and fParentId=@Para order by ftype,fobjectname', NULL),
	(36, 'Form', '翻译窗体列表框2的数据源', 'SysfrmLanguage_sub', 'List_Object', 'Select fID,fcaption,case ftype when 3 then \'Report\' when 2 then \'Form\' end as CtlType from syslanguage where fParentId=@Para order by `ftype`,fobjectname', NULL),
	(39, 'Form', '报价单列表窗体数据源', 'f_Quotation_List_O', 'Form', 'select fOrderID,fOrderDate,fCustomerName,fNUIT,fCity,fConfirmed1,fCanceled1,fEntry_Name,\r\nfConfirm_Name,fCancel_Name,fAmount,fTax,fPayable,fDesconto,fContato,fCelular,fTelefone,fConfirmed,fCustomerID\r\nfrom v_quotation \r\nwhere fOrderID like \'\\@Para%\' and fCanceled=0 and \\@Para\r\norder by fOrderID DESC', 'select fOrderID,fOrderDate,fCustomerName,fNUIT,fCity,fConfirmed1,fCanceled1,fEntry_Name,\r\nfConfirm_Name,fCancel_Name,fAmount,fTax,fPayable,fDesconto,fContato,fCelular,fTelefone,fConfirmed,fCustomerID\r\nfrom v_quotation \r\nwhere fOrderID like \'\\@Para%\' and fCanceled=0 \r\norder by fOrderID DESC'),
	(41, 'Report', '报价单报表数据源', 'r_Quotation', 'Report', 'select Q1.*,Q2.*\r\nfrom \r\n(select LianCi from (select 1 as LianCi union select 2 as LianCi) as Q1 where Lianci<=@Para)  AS Q1,\r\n(select q.QuoteID,q.OrderID,q.CustomerName,q.ChengShi,q.QuoteRiQi,q.LeiBie,q.PinPaiCaiLiao,q.JinE as ZongJinE,q.Shui,q.YingFuJinE,q.Desconto,YanSe,Note,qt.ID,qt.ShuLiang,qt.PinMing,qt.Chang,qt.Kuan,qt.DanJia,qt.JinE as MingXiJinE from \r\nt_quotation as q left join t_quotation_detail as qt on q.QuoteID=qt.QuoteID\r\nwhere q.QuoteID=@Para) as Q2\r\norder by ID', NULL),
	(43, 'Form', '检查一个要删除的客户号是否已经使用', 'f_customer', 'Form', 'select fOrderID from t_order where fOrderID=@Para Limit 1', NULL),
	(44, 'Form', '快速查找客户名称查询', 'f_customer', 'KeyWord', 'select Q.* from (select c.*, concat(IFNULL(fCustomerName,\'\'),\r\nIFNULL(fNUIT,\'\'),\r\nIFNULL(fCity,\'\'),\r\nIFNULL(fContato,\'\'),\r\nIFNULL(fAreaCode,\'\'),\r\nIFNULL(fCelular,\'\'),\r\nIFNULL(fTelefone,\'\'),\r\nIFNULL(fEndereco,\'\'),\r\nIFNULL(fEmail,\'\'),\r\nIFNULL(fWeb,\'\'),\r\nIFNULL(fFax,\'\'))\r\nas F0\r\nfrom t_customer as c)  as Q where Q.F0 like \'%\\@Para%\'', NULL),
	(45, 'Form', '印刷订单列表窗体数据源', 'f_PrintingOrder_List', 'Form', 'select v.fOrderID,v.fCustomerID,v.fOrderDate,v.fEspecie,v.fRequiredDeliveryDate,v.\r\nfBrandMateria,v.fAmount,v.fTax,v.fPayable,v.fDesconto,v.fNumerBegin,v.fQuant,v.\r\nfPagePerVolumn,v.fNumerEnd,v.fAvista,v.fNote,v.fTamanho,v.fSucursal,v.fLogo,v.\r\nfVendedor,v.fNrCopy,v.fContato,v.fCelular,v.fTelefone,v.\r\nfCustomerName,v.fNUIT,v.fCity,v.fSubmited1,v.fSubmited+0 as fSubmited,v.fSubmit_Name,v.fEntry_Name\r\nFROM v_order as v \r\nWHERE fOrderID Like \'\\@Para%\' and fCanceled=0 and \\@Para AND (fSubmited=@Para OR fSubmited=@Para) \r\nORDER BY forderID DESC', 'select v.fOrderID,v.fCustomerID,v.fOrderDate,v.fEspecie,v.fRequiredDeliveryDate,v.\r\nfBrandMateria,v.fAmount,v.fTax,v.fPayable,v.fDesconto,v.fNumerBegin,v.fQuant,v.\r\nfPagePerVolumn,v.fNumerEnd,v.fAvista,v.fNote,v.fTamanho,v.fSucursal,v.fLogo,v.\r\nfVendedor,v.fNrCopy,v.fContato,v.fCelular,v.fTelefone,v.\r\nfCustomerName,v.fNUIT,v.fCity,v.fSubmited1,v.fSubmited+0 as fSubmited,v.fSubmit_Name,v.fEntry_Name\r\nFROM v_order as v \r\nWHERE fOrderID Like \'\\@Para%\' and fCanceled=0 \r\nORDER BY forderID DESC'),
	(47, 'Form', '已印刷单据号列表控件数据源', 'f_PrintingOrder_Edit_Mob', 'NumberList', 'select fOrderID as OrderID,cast(e.fTitle as char(20)) as Especie, cast(fNumerBegin as SIGNED) as NumerBegin,cast(fNumerEnd as SIGNED) as NumerEnd \r\nfrom t_order as o left join t_enumeration as e  on o.fEspecieID=e.fItemID where fCustomerID = @Para And fEspecieID = @Para order by fNumerEnd DESC', NULL),
	(49, 'Form', '欠款列表', 'f_Receivables_CustomerArrearsList', 'Form', 'select c.fCustomerID,\r\n	c.fCustomerName,\r\n	c.fCity,\r\nif(	YS.fYS=0,null,YS.fYS) as fYS,\r\n	if(SK.fSK=0,null,SK.fSK) as fSK,\r\n	if(if(isnull(YS.fYS),0,YS.fYS)-if(isnull(SK.fSK),0,SK.fSK)=0,null,if(isnull(YS.fYS),0,YS.fYS)-if(isnull(SK.fSK),0,SK.fSK)) as fQK,\r\n	r1.fReceiptDate as fLastfReceiptDate,\r\n	cast(r1.fAmountCollected as DECIMAL) as fLastAmountCollected\r\nfrom t_customer as c left join \r\n	(select o.fCustomerID,\r\n		cast(sum(o.fPayable) as DECIMAL) as fYS \r\n		from t_order as o \r\n		where o.fCanceled=0 and o.fSubmited=1 and o.fConfirmed=1 group by o.fCustomerID) as YS\r\n on c.fCustomerID=YS.fCustomerID left join \r\n	(select r.fCustomerID,\r\n		cast(sum(r.fAmountCollected) as DECIMAL) as fSK,\r\n		max(r.fID) as LastRecID \r\n		from t_receivables as r \r\n		group by r.fCustomerID ) as SK\r\n	on c.fCustomerID=SK.fCustomerID\r\n	left join t_receivables as r1 \r\n	on r1.fID=SK.LastRecID\r\nwhere c.fCustomerID=@Para', 'select c.fCustomerID,\r\n	c.fCustomerName,\r\n	c.fCity,\r\n	if(	YS.fYS=0,null,YS.fYS) as fYS,\r\n	if(SK.fSK=0,null,SK.fSK) as fSK,\r\n	if(if(isnull(YS.fYS),0,YS.fYS)-if(isnull(SK.fSK),0,SK.fSK)=0,null,if(isnull(YS.fYS),0,YS.fYS)-if(isnull(SK.fSK),0,SK.fSK)) as fQK,\r\n	r1.fReceiptDate as fLastfReceiptDate,\r\n	cast(r1.fAmountCollected as DECIMAL) as fLastAmountCollected\r\nfrom t_customer as c left join \r\n	(select o.fCustomerID,\r\n		cast(sum(o.fPayable) as DECIMAL) as fYS \r\n		from t_order as o \r\n		where o.fCanceled=0 and o.fSubmited=1 and o.fConfirmed=1 group by o.fCustomerID) as YS\r\n on c.fCustomerID=YS.fCustomerID left join \r\n	(select r.fCustomerID,\r\n		cast(sum(r.fAmountCollected) as DECIMAL) as fSK,\r\n		max(r.fID) as LastRecID \r\n		from t_receivables as r \r\n		group by r.fCustomerID ) as SK\r\n	on c.fCustomerID=SK.fCustomerID\r\n	left join t_receivables as r1 \r\n	on r1.fID=SK.LastRecID'),
	(50, 'Form', '某客户的交易记录', 'f_Receivables_CustomerRecorder', NULL, 'select Q.* from (select o.fOrderDate as fDate,o.fOrderID,cast(o.fPayable as DECIMAL) as fPayable,null as fAmountCollected,o.ts  from t_order as o where o.fCustomerID=@Para and o.fCanceled=0 and o.fSubmited=1 and o.fConfirmed=1\r\nunion all\r\nselect r.fReceiptDate as fDate,Null as fOrderID,Null as fPayable,cast(r.fAmountCollected as DECIMAL) as fAmountCollected,r.ts from   t_receivables as r where r.fCustomerID=@Para\r\nunion all\r\nselect null as fDate, \'Sum\' as fOrderID,  cast(sum(Q1.fPayable) as DECIMAL) as fPayable,cast(sum(Q1.fAmountCollected) as DECIMAL) as fAmountCollected,null as ts\r\nfrom (select o.fOrderDate as fDate,o.fOrderID,cast(o.fPayable as DECIMAL) as fPayable ,null as fAmountCollected,o.ts  from t_order as o where o.fCustomerID=@Para and o.fCanceled=0 and o.fSubmited=1 and o.fConfirmed=1\r\nunion all\r\nselect r.fReceiptDate as fDate,Null as fOrderID,Null as fPayable,cast(r.fAmountCollected as DECIMAL) ,r.ts from   t_receivables as r where r.fCustomerID=@Para) as Q1\r\n\r\n) as Q\r\norder by Q.TS DESC', NULL),
	(51, 'Form', '某日收款明细', 'f_Receivables_CurrentDayRec', 'Form', 'select r.* from v_receivables as r where  Date_Format(r.fReceiptDate,\'%Y-%m-%d\')=\\@Para order by fID DESC', NULL),
	(52, 'Form', NULL, 'f_CustomerSelecter', 'List_Value', 'select fCustomerID,fCustomerName from t_customer where fCustomerName like \'%\\@Para%\'', 'select fCustomerID,fCustomerName from t_customer where fCustomerName=\'\''),
	(53, 'Form', '每日收款统计报表 基于 付款书', 'f_report_rec_day_List', 'Form', 'select if(isnull(Q3.d),\'Sum\',Q3.d) as Day0 ,M1,M2,M3,M4,M5,M6,M7,M8,M9,M10,M11,M12\r\nfrom (select Q2.d, \r\nFormat(SUM(Q2.M1_),2) AS M1, \r\nformat(SUM(Q2.M2_),2)   AS M2, \r\nFormat(SUM(Q2.M3_),2)  AS M3, \r\nFormat(SUM(Q2.M4_),2)  AS M4, \r\nFormat(SUM(Q2.M5_),2)  AS M5, \r\nFormat(SUM(Q2.M6_),2)  AS M6, \r\nFormat(SUM(Q2.M7_),2)  AS M7, \r\nFormat(SUM(Q2.M8_),2)  AS M8, \r\nFormat(SUM(Q2.M9_),2)  AS M9, \r\nFormat(SUM(Q2.M10_),2)  AS M10, \r\nFormat(SUM(Q2.M11_),2)  AS M11, \r\nFormat(SUM(Q2.M12_),2)  AS M12\r\nfrom (select Q1.d,\r\nIF(Q1.m=1,Q1.j1, NULL) AS M1_,\r\nIF(Q1.m=2,Q1.j1, NULL) AS M2_,\r\nIF(Q1.m=3,Q1.j1, NULL) AS M3_,\r\nIF(Q1.m=4,Q1.j1, NULL) AS M4_,\r\nIF(Q1.m=5,Q1.j1, NULL) AS M5_,\r\nIF(Q1.m=6,Q1.j1, NULL) AS M6_,\r\nIF(Q1.m=7,Q1.j1, NULL) AS M7_,\r\nIF(Q1.m=8,Q1.j1, NULL) AS M8_,\r\nIF(Q1.m=9,Q1.j1, NULL) AS M9_,\r\nIF(Q1.m=10,Q1.j1, NULL) AS M10_,\r\nIF(Q1.m=11,Q1.j1, NULL) AS M11_,\r\nIF(Q1.m=12,Q1.j1, NULL) AS M12_\r\nfrom (SELECT MONTH(fOrderDate) AS m, DAY(fOrderDate) AS d, SUM(fPayable) AS j1\r\nFROM t_order\r\nWHERE Year(fOrderDate)=@Para and fCanceled=0 and fSubmited=1 and fConfirmed=1\r\nGROUP BY MONTH(fOrderDate), DAY(fOrderDate)) AS Q1) As Q2\r\ngroup by Q2.d WITH ROLLUP) as Q3', NULL),
	(54, 'Other', '取数据表结构用SQL，只取结构不取数据', NULL, NULL, 'Select * from \\@Para Limit 0', NULL),
	(55, 'Other', '枚举值列表', NULL, NULL, 'Select fItemID,fTitle,fSpare1,fSpare2 from t_enumeration where fTypeID=@Para and fTitle like \'%\\@Para%\'', NULL);
